import { PageHeader, Button, Table, Space, Modal  } from 'antd';

import React, {useState} from 'react';

import Layouts  from '../../components/Layout/Layouts';

import { FiUserPlus } from "react-icons/fi";

import { AiOutlineClose, AiOutlineEdit, AiOutlineUser, AiOutlineSearch } from "react-icons/ai";


const columns = [
    {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
    },
    {
        title: 'Address',
        dataIndex: 'address',
        key: 'address',
    },
    {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
        <Space size="middle">
            <a><AiOutlineUser /> </a>
            <a><AiOutlineEdit /> </a>
            <a><AiOutlineClose /> </a>
        </Space>
        ),
    },
];

const data = [
    {
      key: '1',
      name: 'John Brown',
      age: 32,
      address: 'New York No. 1 Lake Park',
      tags: ['nice', 'developer'],
    },
    {
      key: '2',
      name: 'Jim Green',
      age: 42,
      address: 'London No. 1 Lake Park',
      tags: ['loser'],
    },
    {
      key: '3',
      name: 'Joe Black',
      age: 32,
      address: 'Sidney No. 1 Lake Park',
      tags: ['cool', 'teacher'],
    },
];


const Patient = () => {

    const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    return (
        <Layouts>

            <PageHeader
                title='Patient'
                extra={[
                    <Button size="large"><AiOutlineSearch /></Button>,
                    <Button 
                    size="large" 
                    onClick={showModal} 

                    >Add Patient</Button>]
                }
            > 
            </PageHeader>

            <Modal title="Basic Modal" 
            visible={isModalVisible} 
            onOk={handleOk} 
            onCancel={handleCancel}>
                <p>Some contents...</p>
                <p>Some contents...</p>
                <p>Some contents...</p>
            </Modal>

            <Table className='table' columns={columns} dataSource={data} />


        </Layouts>
    )

}

export default Patient;
