import { Col, Layout, Button, Row, Menu } from 'antd';
import React, { useState } from 'react';
import { CgMenuLeftAlt } from 'react-icons/cg';
import { FiLogOut } from "react-icons/fi";
import { Link } from 'react-router-dom';
import './Layout.css';

const { Header, Footer, Content } = Layout;


const [visible, setVisible] = useState(false);

const showDrawer = () => {
    setVisible(true);
}

const onClose = () => {
    setVisible(false);
}

const Layouts = (props) => {
    return (
        <>

            <Header className='header'>
                <Row>
                    <Col span={8}>
                        <Button className='menu-btn' onClick={showDrawer}>
                            <CgMenuLeftAlt />
                        </Button> 
                    </Col>
                    <Col span={7} offset={8} className='nav-right'> 
                        <Link to='/login'> Logout <FiLogOut  /> </Link> 
                    </Col>
                </Row>
            </Header>

            <Drawer 
                placement="right"
                closable={false}
                onClose={onClose}
                visible={visible}>

                <Menu>
                    <Menu.Item key={1}><Link to="/">მთავარი</Link></Menu.Item>
                    <Menu.Item key={2}><Link to="/tests">ტესტები</Link></Menu.Item>
                    <Menu.Item key={3}><Link to="/blogs">ბლოგი</Link></Menu.Item>
                    <Menu.Item key={4}><Link to="/contact">კონტაქტი</Link></Menu.Item>
                </Menu>

            </Drawer>


            <Content>{props.children}</Content>

        </>
    )
}
export default Layouts;
